tinymce.PluginManager.add("lineheight", function(t, e) {
    t.addButton("lineheight", function(){
        var items = [];
        items.push({text:'100%',value:'100%'});
        items.push({text:'110%',value:'110%'});
        items.push({text:'120%',value:'120%'});
        items.push({text:'130%',value:'130%'});
        items.push({text:'140%',value:'140%'});
		items.push({text:'150%',value:'150%'});
		items.push({text:'160%',value:'160%'});
		items.push({text:'180%',value:'180%'});
		items.push({text:'200%',value:'200%'});
		items.push({text:'220%',value:'220%'});
		items.push({text:'240%',value:'240%'});
		items.push({text:'260%',value:'260%'});
		items.push({text:'260%',value:'280%'});
        return {
            type : 'listbox',
            text : "行间距",
            values : items,
            onselect:function(e){
                if(e.control.settings.value){
                    var ed = tinymce.activeEditor;
                    var node = ed.selection.getNode();
                    var text = ed.selection.getContent({'format':'text'});
                    if (node && text) {
                        ed.dom.setStyle(node,'lineHeight',e.control.settings.value);
                    } else {
                        alert('请选择需要更改的内容');
                    }
                }
            }
        };
    })
});